import { Component } from '@angular/core';
import { usuario } from './usuario.model';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  users: usuario[]=[
    new usuario ("Osama bin Laden"),
    new usuario ("Naruto Uzumaki"),
    new usuario ("Junker Otto Eduard Leopold von Bismark")
  ]

  cuadroNombre:string="";
  agregarSe: any;

  agregar_usuario(){
    let mi_usuario = new usuario(this.cuadroNombre);
    this.users.push(mi_usuario);
    }

  usuario_vacío: string="No has puesto nada, perro o perra judío o judía.";
  mostrar_vacío: boolean=false;

  process_button(){
    if (this.cuadroNombre===""){this.usuario_vacío=true}
    else {this.agregar_usuario()}
  }
}
